import React from 'react';

const RewardDisplay = ({ reward }) => {
    if (!reward) return null;

    const rankColors = {
        LEGENDARY: {
            color: "from-orange-600 via-yellow-500 to-red-600",
            glow: "shadow-[0_0_30px_rgba(249,115,22,0.6)]",
            textColor: "text-orange-400",
            icon: <Trophy className="w-6 h-6 animate-bounce" />
        },
        EPIC: {
            color: "from-purple-600 via-fuchsia-500 to-indigo-600",
            glow: "shadow-[0_0_20px_rgba(168,85,247,0.5)]",
            textColor: "text-purple-400",
            icon: <Sparkles className="w-5 h-5 animate-pulse" />
        },
        RARE: {
            color: "from-blue-600 to-cyan-500",
            glow: "shadow-[0_0_15px_rgba(59,130,246,0.4)]",
            textColor: "text-blue-400",
            icon: <Zap className="w-5 h-5" />
        },
        COMMON: "text-gray-400 border-gray-400"
    };

    return (
        <div className={`mt-6 p-6 border-4 bg-gray-900 rounded-xl ${rankColors[reward.rank]}`}>
            <div className="text-sm font-bold tracking-tighter uppercase">{reward.rank} ITEM FOUND</div>
            <h3 className="text-2xl font-black my-2">{reward.cardName}</h3>
            <p className="italic text-gray-300">"{reward.reason}"</p>
            <div className="mt-4 text-4xl font-mono">+{reward.rewardValue * 100}% Yield</div>
        </div>
    );
};

export default RewardDisplay;
